import React,{Component} from 'react';
import {Table} from 'react-bootstrap';

import {Button,ButtonToolbar} from 'react-bootstrap';
import {AddProduktet} from './AddProduktet';
import {EditProduktet} from './EditProduktet';

export class Produktet extends Component{

    constructor(props){
        super(props);
        this.state={prod:[], addModalShow:false, editModalShow:false}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'produktet')
        .then(response=>response.json())
        .then(data=>{
            this.setState({prod:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    deleteEmp(proid){
        if(window.confirm('A jeni i sigurte?')){
            fetch(process.env.REACT_APP_API+'produktet/'+proid,{
                method:'DELETE',
                header:{'Accept':'application/json',
            'Content-Type':'application/json'}
            })
        }
    }
    render(){
        const {prod, proid,proemri,proct,procs,propershkrimi,prokomp,profoto}=this.state;
        let addModalClose=()=>this.setState({addModalShow:false});
        let editModalClose=()=>this.setState({editModalShow:false});
        return(
            <div >
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>ID e Produktit</th>
                        <th>Emri i Produktit</th>
                        <th>Cmimi ne Treg</th>
                        <th>Cmimi Startues</th>
                        <th>Pershkrimi</th>
                        <th>Kompania</th>
                        <th>Foto</th>
                        <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {prod.map(pro=>
                            <tr key={emp.EmployeeId}>
                                <td>{emp.EmployeeId}</td>
                                <td>{emp.EmployeeName}</td>
                                <td>{emp.Department}</td>
                                <td>{emp.DateOfJoining}</td>
                                <td>
<ButtonToolbar>
    <Button className="mr-2" variant="info"
    onClick={()=>this.setState({editModalShow:true,
        empid:emp.EmployeeId,empname:emp.EmployeeName,depmt:emp.Department,
        photofilename:emp.PhotoFileName,doj:emp.DateOfJoining})}>
            Edit
        </Button>

        <Button className="mr-2" variant="danger"
    onClick={()=>this.deleteEmp(emp.EmployeeId)}>
            Delete
        </Button>

        <EditEmpModal show={this.state.editModalShow}
        onHide={editModalClose}
        empid={empid}
        empname={empname}
        depmt={depmt}
        photofilename={photofilename}
        doj={doj}
        />
</ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>

                <ButtonToolbar>
                    <Button variant='primary'
                    onClick={()=>this.setState({addModalShow:true})}>
                    Add Employee</Button>

                    <AddEmpModal show={this.state.addModalShow}
                    onHide={addModalClose}/>
                </ButtonToolbar>
            </div>
        )
    }
}
