import React,{Component} from 'react';
import {Table} from 'react-bootstrap';

import {Button,ButtonToolbar,Col,Row,Form} from 'react-bootstrap';

export class Home extends Component{
    constructor(props){
        super(props);
        this.state={prod:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'produktet')
        .then(response=>response.json())
        .then(data=>{
            this.setState({prod:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }
    render(){
        const {prod}=this.state;

        return(
            <div className="container-fluid">
                <Row>

                    <div className="produktet">
                      {prod.map(pro=>
                        <div>
                            <p>pro.Foto</p>
                            <p>{pro.Emri}</p>
                            <p>{pro.CmimiStartues}â‚¬</p>                                                
                       </div>
                       )}
                    </div>
                
                
                </Row>
            </div>
        )
    }
}
