import React,{Component} from 'react';
import {Table} from 'react-bootstrap';
import {Link} from 'react-router';
import {Button,ButtonToolbar} from 'react-bootstrap';

export class Kompania extends Component{

    constructor(props){
        super(props);
        this.state={komp:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'kompania')
        .then(response=>response.json())
        .then(data=>{
            this.setState({komp:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    deleteKom(komid){
        if(window.confirm('A jeni i sigurte?')){
            fetch(process.env.REACT_APP_API+'kompania/'+komid,{
                method:'DELETE',
                header:{'Accept':'application/json',
            'Content-Type':'application/json'}
            })
        }
    }
    render(){
        const {komp, komid,komemri,komnrb,komvendi,komtel}=this.state;
        return(
           
           <div>
               <a href="/personat">Personat</a>
               <a href="/">Produktet</a>
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                        <th>Id e KompanisÃ«</th>
                        <th>Emri</th>
                        <th>Nr i Biznesit</th>
                        <th>Vendi</th>
                        <th>Nr i Telefonit</th>
                        <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {komp.map(kom=>
                            <tr key={kom.KompaniaId}>
                                <td>{kom.KompaniaId}</td>
                                <td>{kom.Emri}</td>
                                <td>{kom.NrBiznesit}</td>
                                <td>{kom.Vendi}</td>
                                <td>{kom.NrTelefonit}</td>                                
                                <td>
<ButtonToolbar>
    <Button className="mr-2" variant="info"
    onClick={()=>this.setState({
        komid:kom.KompaniaId,komemri:kom.Emri,komnrb:kom.NrBiznesit,
        komvendi:kom.Vendi,komtel:kom.NrTelefonit})}>
            Modifiko
        </Button>

        <Button className="mr-2" variant="danger"
    onClick={()=>this.deleteKom(kom.KompaniaId)}>
            Fshij
        </Button>

       
</ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>

               
            </div>
           
        )
    }
}
