import React,{Component} from 'react';
import  './Home.css';
import {Button,ButtonToolbar,Col,Row,Form} from 'react-bootstrap';
import { Produktet } from './Produktet';

export class Home extends Component{
    constructor(props){
        super(props);
        this.state={prod:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'produktet')
        .then(response=>response.json())
        .then(data=>{
            this.setState({prod:data});
        });
    }

   
        componentDidMount() {
            fetch('/api/produktet')
              .then(response => response.json())
              .then(json => this.setState({ users: json.data }));
          }
    

    componentDidUpdate(){
        this.refreshList();
    }
    render(){
        const {prod}=this.state;

        return(
            <div className="container-fluid p">
                <Row>
        {
          this.state.users.length == 0
            ? 'Loading users...'
            : this.state.users.map(user => (
              <figure key={user.produktetId}>
                <img src={user.Foto} />
                <figcaption>
                  {user.emri}
                </figcaption>
              </figure>
            ))
        }
                    
                      {prod.map(pro=>
                        <div className="produktet">
                            <p class="foto">{pro.Foto}</p>
                            <p class="emri">{pro.Emri}</p>
                        <hr class="hr"></hr>
                            <p class="cmimi">{pro.CmimiStartues}â‚¬</p>                                                
                       </div>
                       )}
                    
                
                
                </Row>
            </div>
        )
    }
}
