import React,{Component} from 'react';
import {Modal,Button, Row, Col, Form,Image} from 'react-bootstrap';

export class EditProduktet extends Component{
    constructor(props){
        super(props);
        this.state={komp:[]};
        this.handleSubmit=this.handleSubmit.bind(this);
        this.handleFileSelected=this.handleFileSelected.bind(this);
    }

    foto = "laptop.png";
    imagesrc = process.env.REACT_APP_PHOTOPATH+this.foto;

    componentDidMount(){
        fetch(process.env.REACT_APP_API+'kompania')
        .then(response=>response.json())
        .then(data=>{
            this.setState({komp:data});
        });
    }

    handleSubmit(event){
        event.preventDefault();
        fetch(process.env.REACT_APP_API+'produktet',{
            method:'PUT',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                ProduktetId:event.target.ProduktetId.value,
                Emri:event.target.Emri.value,
                CmimiNeTreg:event.target.CmimiNeTreg.value,
                CmimiStartues:event.target.CmimiStartues.value,
                Pershkrimi:event.target.Pershkrimi.value,
                Kompania:event.target.Kompania.value,
                Foto:this.Foto

            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert('Failed');
        })
    }


    handleFileSelected(event){
        event.preventDefault();
        this.Foto=event.target.files[0].name;
        const formData = new FormData();
        formData.append(
            "myFile",
            event.target.files[0],
            event.target.files[0].name
        );

        fetch(process.env.REACT_APP_API+'Produktet/SaveFile',{
            method:'POST',
            body:formData
        })
        .then(res=>res.json())
        .then((result)=>{
            this.imagesrc=process.env.REACT_APP_PHOTOPATH+result;
        },
        (error)=>{
            alert('Failed');
        })
        
    }

    render(){
        return (
            <div className="container">

<Modal
{...this.props}
size="lg"
aria-labelledby="contained-modal-title-vcenter"
centered
>
    <Modal.Header clooseButton>
        <Modal.Title id="contained-modal-title-vcenter">
            Modifiko Produktet
        </Modal.Title>
    </Modal.Header>
    <Modal.Body>

        <Row>
            <Col sm={6}>
                <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="ProduktetId">
                        <Form.Label>ProduktetId</Form.Label>
                        <Form.Control type="text" name="ProduktetId" required 
                        placeholder="ProduktetId"
                        disabled
                        defaultValue={this.props.proid}/>
                    </Form.Group>

                    <Form.Group controlId="Emri">
                        <Form.Label>Emri</Form.Label>
                        <Form.Control type="text" name="Emri" required 
                        defaultValue={this.props.proemri}
                        placeholder="Emri"/>
                    </Form.Group>

                    <Form.Group controlId="CmimiStartues">
                        <Form.Label>CmimiStartues</Form.Label>
                        <Form.Control type="text" name="CmimiStartues" required 
                        defaultValue={this.props.procs}
                        placeholder="CmimiStartues"/>
                    </Form.Group>

                    <Form.Group controlId="Cmimi Ne Treg">
                        <Form.Label>Cmimi Ne Treg</Form.Label>
                        <Form.Control 
                        type="text"
                        name="Cmimi Ne Treg"
                        required
                        placeholder="Cmimi Ne Treg"
                        defaultValue={this.props.proct}
                        />
                        </Form.Group>

                    <Form.Group controlId="Pershkrimi">
                        <Form.Label>Pershkrimi</Form.Label>
                        <Form.Control type="text" name="Pershkrimi" required 
                        defaultValue={this.props.propershkrimi}
                        placeholder="Pershkrimi"/>
                    </Form.Group>

                    <Form.Group controlId="Kompania">
                        <Form.Label>Kompania</Form.Label>
                        <Form.Control as="select" defaultValue={this.props.prokompania}>
                        {this.state.komp.map(kom=>
                            <option key={kom.KompaniaId}>{kom.Emri}</option>)}
                        </Form.Control>
                    </Form.Group>

                    <Form.Group>
                        <Button variant="primary" type="submit">
                        Modifiko Produktin
                        </Button>
                    </Form.Group>
                </Form>
            </Col>

            <Col sm={6}>
                <Image width="200px" height="200px"
                src={process.env.REACT_APP_PHOTOPATH+this.props.foto}/>
                <input onChange={this.handleFileSelected} type="File"/>
            </Col>
        </Row>
    </Modal.Body>
    
    <Modal.Footer>
        <Button variant="danger" onClick={this.props.onHide}>Close</Button>
    </Modal.Footer>

</Modal>

            </div>
        )
    }

}
