import React,{Component} from 'react';
import {Table} from 'react-bootstrap';

import {Button,ButtonToolbar} from 'react-bootstrap';

export class Produktet extends Component{

    constructor(props){
        super(props);
        this.state={prod:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'produktet')
        .then(response=>response.json())
        .then(data=>{
            this.setState({prod:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    deletePer(perid){
        if(window.confirm('A jeni i sigurte?')){
            fetch(process.env.REACT_APP_API+'produktet/'+perid,{
                method:'DELETE',
                header:{'Accept':'application/json',
            'Content-Type':'application/json'}
            })
        }
    }
    render(){
        const {prod, proid,proemri,proct,procs,propershkrimi
            ,prokompania,profoto}=this.state;
        return(
            <div >              
               <a class="linkat" href="/produktet">Produktet</a>
               <a class="linkat" href="/personat">Personat</a>
               <a class="linkat" href="/kompania">Kompania</a>
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                        <th>Id e Produktit</th>
                        <th>Emri</th>
                        <th>Cmimi ne Treg</th>
                        <th>Cmimi ne Ankand</th>
                        <th>Pershkrimi</th>
                        <th>Kompania</th>
                        <th>Foto</th>
                        <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {prod.map(pro=>
                            <tr key={pro.ProduktetId}>
                                <td>{pro.ProduktetId}</td>
                                <td>{pro.Emri}</td>
                                <td>{pro.CmimiNeTreg}</td>
                                <td>{pro.CmimiStartues}</td>
                                <td>{pro.Pershkrimi}</td>
                                <td>{pro.Kompania}</td>
                                <td>{pro.Foto}</td>                                
                                <td>
<ButtonToolbar>
    <Button className="mr-2" variant="info"
    onClick={()=>this.setState({
        proid:pro.ProduktetId,proemri:pro.Emri,proct:pro.CmimiNeTreg,procs:pro.CmimiStartues,
        propershkrimi:pro.Pershkrimi,prokompania:pro.Kompania,})}>
            Modifiko
        </Button>

        <Button className="mr-2" variant="danger"
    onClick={()=>this.deletePer(pro.ProduktetId)}>
            Fshij
        </Button>

       
</ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>

               
            </div>
        )
    }
}
