import React,{Component} from 'react';
import  './Home.css';
import {Button,ButtonToolbar,Col,Row,Form} from 'react-bootstrap';

export class Home extends Component{
    constructor(props){
        super(props);
        this.state={prod:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'produktet')
        .then(response=>response.json())
        .then(data=>{
            this.setState({prod:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }
    render(){
        const {prod}=this.state;

        return(
            <div className="container-fluid p">
                <Row>

                    
                      {prod.map(pro=>
                        <div className="produktet">
                            <p class="foto">pro.Foto</p>
                            <p class="emri">{pro.Emri}</p>
                        
                            <p class="cmimi">{pro.CmimiStartues}â‚¬</p>                                                
                       </div>
                       )}
                    
                
                
                </Row>
            </div>
        )
    }
}
