import React,{Component} from 'react';
import {Table} from 'react-bootstrap';

import {Button,ButtonToolbar} from 'react-bootstrap';
import {EditProduktet} from './EditProduktet';

export class Produktet extends Component{

    constructor(props){
        super(props);
        this.state={prod:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'produktet')
        .then(response=>response.json())
        .then(data=>{
            this.setState({prod:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    deleteEmp(proid){
        if(window.confirm('A jeni i sigurte?')){
            fetch(process.env.REACT_APP_API+'produktet/'+proid,{
                method:'DELETE',
                header:{'Accept':'application/json',
            'Content-Type':'application/json'}
            })
        }
    }
    render(){
        const {prod, proid,proemri,proct,procs,propershkrimi,prokomp,profoto}=this.state;
        return(
            <div >
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>ID e Produktit</th>
                        <th>Emri i Produktit</th>
                        <th>Cmimi ne Treg</th>
                        <th>Cmimi Startues</th>
                        <th>Pershkrimi</th>
                        <th>Kompania</th>
                        <th>Foto</th>
                        <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                       {prod.map(pro=>
                            <tr key={pro.ProduktetId}>
                                <td>{pro.ProduktetId}</td>
                                <td>{pro.Emri}</td>                                
                                <td>{pro.CmimiNeTreg}€</td>
                                <td>{pro.CmimiStartues}€</td>
                                <td>{pro.Pershkrimi}</td>
                                <td>{pro.Kompania}</td>
                                <td>{pro.Foto}</td>
                                <td>
                                                        
<ButtonToolbar>
    <Button className="mr-2" variant="info"
    onClick={()=>this.setState({editModalShow:true,
        proid:pro.ProduktetId,proemri:pro.Emri,proct:pro.CmimiNeTreg,
        procs:pro.CmimiStartues,propershkrimi:pro.Pershkrimi,prokomp:pro.Kompania,profoto:pro.Foto
        })}> 
            Modifiko
        </Button>

        <Button className="mr-2" variant="danger"
    onClick={()=>this.deleteEmp(pro.ProduktetId)}>
            Fshij
        </Button>

        <EditProduktet show={this.state.editModalShow}
        onHide={editModalClose}
        proid={proid}
        proemri={proemri}
        proct={proct}
        procs={procs}
        propershkrimi={propershkrimi}
        prokomp={prokomp}
        profoto={profoto}
        />
    </ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>
            </div>
        )
    }
}
