    constructor(props){
        super(props);
        this.state={pers:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'personat')
        .then(response=>response.json())
        .then(data=>{
            this.setState({pers:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

